package com.gdproj.utils;

import cn.hutool.core.util.CharsetUtil;
import cn.hutool.core.util.StrUtil;
import cn.hutool.crypto.asymmetric.KeyType;
import cn.hutool.crypto.asymmetric.RSA;

public class RSAUtil {
    private static final String PUBLIC_KEY = "";
    private static final String PRIVATE_KEY = "";

    //默认密码 000000
    public static final String DEFAULT_PASSWORD = "";
    private static final RSA rsa_en = new RSA(null,PUBLIC_KEY);
    private static final RSA rsa_de = new RSA(PRIVATE_KEY,null);


    public static String encrypt(String data){
        String encrypt = rsa_en.encryptBase64(StrUtil.bytes(data, CharsetUtil.CHARSET_UTF_8),KeyType.PublicKey);
        return encrypt;
    }

    public static String decrypt(String password){
        String decrypt = rsa_de.decryptStr(password, KeyType.PrivateKey);
        return decrypt;
    }
}